    def send_line(self, s: str):
        if not s.endswith('\n'):
            s += '\n'
        self.f.write(s.encode())

    def recv_line(self) -> str:
        line = self.f.readline()
        if not line:
            raise ConnectionError("Short read from server")
        return line.decode(errors='replace')

