    Fb = build_single_freq(T2, bits, high)
    Fc = build_single_freq(T3, bits, high)
    Fd = build_single_freq(T4, bits, high)
    C = build_single_freq(T5, bits, high)
    if label:
        print(f"[i] Recover {label}: FWHT transforms")
