    fwht_xor(Fa)
    fwht_xor(Fb)
    fwht_xor(Fc)
    fwht_xor(Fd)
    fwht_xor(C)
    size = _sage_const_1  << bits
    mask = int(size - _sage_const_1 )
    shift_amount = int(_sage_const_32  - bits if high else _sage_const_0 )
